    const { body } = ctx.request;
    try {
      //body.username->database->email
      let result = await send({
        code: "1234",
        expire: moment().add(30, "minutes").format("YYYY-MM-DD HH:mm:ss"),
        email: body.username,
        user: "ludaohua",
      });
      ctx.body = {
        code: 200,
        data: result,
        msg: "邮件发送成功",
      };
    } catch (e) {
      console.log(e);
    }
  }
  async login(ctx) {
    //接受用户的数据
    //返回token
    // const { body } = ctx.request;
    const body = {
      username: "brian@toimc.com",
      password: "123456",
      sid: "0fa2b2e3-3f87-4e37-b99e-1446d68b4f10",
