      let user = await User.findOne({ username: body.username });
      console.log(user, "user");
      if (user.password === body.password) {
        checkUserPasswd = true;
      }
      //mongoDB查库
      if (checkUserPasswd) {
        let token = jsonwebtoken.sign({ _id: "brian" }, config.JWT_SECRET, {
          expiresIn: "1d",
        });
        ctx.body = {
          code: 200,
          token: token,
        };
      } else {
        //用户名，密码验证失败，返回提示
