          code: 404,
          msg: "用户名或密码错误",
        };
      }
    } else {
      //图片验证码校验失败
      ctx.body = {
        code: 401,
        msg: "图片验证码不正确，请检查",
      };
    }
  }
  async regist(ctx) {}
}
export default new LoginController();

